import React, { useState } from "react";
import "./metodoPago.css";

function MetodoPago() {
  const [metodo, setMetodo] = useState("");
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");

  const datosPago = {
    "Banco Pichincha": {
      descripcion: "Transferencia bancaria a la siguiente cuenta:",
      cuenta: "Cuenta Corriente: 2200154789",
      titular: "Juan P√©rez",
      cedula: "C√©dula: 0102030405",
      imagen: "/img/BancoPichincha.png"
    },
    "De Una": {
      descripcion: "Pago r√°pido a trav√©s de la app De Una:",
      cuenta: "N√∫mero de cuenta: 0998765432",
      titular: "Maria G√≥mez",
      cedula: "C√©dula: 1102030405",
      imagen: "/img/DeUna!.png"
    },
    "Produbanco": {
      descripcion: "Transferencia a Produbanco:",
      cuenta: "Cuenta Ahorros: 4567891230",
      titular: "Carlos Torres",
      cedula: "C√©dula: 1702030405",
      imagen: "/img/Produbanco.png"
    },
    "Efectivo": {
      descripcion: "Paga en efectivo al momento de recibir el producto o en nuestras oficinas.",
      cuenta: "",
      titular: "",
      cedula: "",
      imagen: "/img/efectivo-icon.png"
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!metodo) {
      alert("Por favor, selecciona un m√©todo de pago.");
      return;
    }
    if (!nombre.trim() || !correo.trim()) {
      alert("Por favor, ingresa tu nombre y correo.");
      return;
    }
    alert(`‚úÖ Has seleccionado: ${metodo}\nüë§ Nombre: ${nombre}\nüìß Correo: ${correo}`);
  };

  // Opci√≥n m√©todo
  const crearOpcionMetodo = (opcion) => (
    <label
      key={opcion}
      className={`metodo-option ${metodo === opcion ? "active" : ""}`}
    >
      <input
        type="radio"
        value={opcion}
        checked={metodo === opcion}
        onChange={(e) => setMetodo(e.target.value)}
      />
      <div className="metodo-content">
        <img
          src={datosPago[opcion].imagen}
          alt={opcion}
          className="metodo-imagen"
          onError={(e) => (e.target.style.display = "none")}
        />
        <span className="metodo-nombre">{opcion}</span>
      </div>
    </label>
  );

  // Info pago
  const crearInfoPago = () => {
    if (!metodo) return null;
    const datos = datosPago[metodo];

    return (
      <div className="info-pago">
        <div className="info-header">
          <img src={datos.imagen} alt={metodo} className="info-imagen" />
          <h3>{`Instrucciones para ${metodo}`}</h3>
        </div>
        <div className="info-details">
          <p>{datos.descripcion}</p>
          {datos.cuenta && (
            <div className="cuenta-info">
              <p>
                <b>{datos.cuenta}</b>
              </p>
            </div>
          )}
          {datos.titular && (
            <p>
              <b>Titular: </b>
              {datos.titular}
            </p>
          )}
          {datos.cedula && (
            <p>
              <b>{datos.cedula}</b>
            </p>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="home-container">
      <h1>Selecciona tu m√©todo de pago</h1>

      <form onSubmit={handleSubmit} className="metodo-pago-form">
        {/* Nombre */}
        <input
          type="text"
          placeholder="Ingresa tu nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          className="input-cliente"
        />

        {/* Correo */}
        <input
          type="email"
          placeholder="Ingresa tu correo"
          value={correo}
          onChange={(e) => setCorreo(e.target.value)}
          className="input-cliente"
        />

        {/* M√©todos */}
        {Object.keys(datosPago).map((opcion) => crearOpcionMetodo(opcion))}

        <button type="submit" className="btn-confirmar">
          Confirmar m√©todo
        </button>
      </form>

      {crearInfoPago()}
    </div>
  );
}

export default MetodoPago;
