import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000";

const Perfil = () => {
  const [user, setUser] = useState({
    nombre: "",
    correo: "",
    foto: "",
  });
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");
  const [fotoFile, setFotoFile] = useState(null);
  const [mensaje, setMensaje] = useState("");

  const token = localStorage.getItem("token"); // Asegúrate de guardar el JWT al hacer login

  useEffect(() => {
    if (!token) return;

    axios
      .get(`${API_URL}/api/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setUser(res.data);
        setNombre(res.data.nombre);
        setCorreo(res.data.correo);
      })
      .catch((err) => {
        console.error(err);
        setMensaje("❌ Error al cargar perfil");
      });
  }, [token]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `${API_URL}/api/profile`,
        { nombre, correo },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser({ ...user, nombre, correo });
      setMensaje("✅ Perfil actualizado");
    } catch (err) {
      console.error(err);
      setMensaje("❌ Error al actualizar perfil");
    }
  };

  const handleUploadPhoto = async (e) => {
    e.preventDefault();
    if (!fotoFile) return;

    const formData = new FormData();
    formData.append("foto", fotoFile);

    try {
      const res = await axios.post(`${API_URL}/api/profile/photo`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      setUser({ ...user, foto: res.data.foto });
      setMensaje("✅ Foto actualizada");
    } catch (err) {
      console.error(err);
      setMensaje("❌ Error al subir foto");
    }
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh", fontFamily: "Arial, sans-serif" }}>
      {/* Sidebar */}
      <div style={{ width: "250px", background: "#1a1a1a", color: "#fff", padding: "20px" }}>
        <h2 style={{ textAlign: "center", marginBottom: "40px" }}>Perfil</h2>
        <img
          src={user.foto ? `${API_URL}${user.foto}` : "/default-avatar.png"}
          alt="Foto de perfil"
          style={{ width: "150px", height: "150px", borderRadius: "50%", display: "block", margin: "0 auto 20px" }}
        />
        <p><strong>Nombre:</strong> {user.nombre}</p>
        <p><strong>Correo:</strong> {user.correo}</p>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: "40px", background: "#f4f4f4" }}>
        <h2>Editar Perfil</h2>
        {mensaje && <p>{mensaje}</p>}

        {/* Formulario Nombre/Correo */}
        <form onSubmit={handleUpdateProfile} style={{ marginBottom: "30px" }}>
          <div style={{ marginBottom: "15px" }}>
            <label>Nombre:</label><br />
            <input
              type="text"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              style={{ width: "300px", padding: "8px" }}
            />
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label>Correo:</label><br />
            <input
              type="email"
              value={correo}
              onChange={(e) => setCorreo(e.target.value)}
              style={{ width: "300px", padding: "8px" }}
            />
          </div>
          <button type="submit" style={{ padding: "10px 20px", background: "#3498db", color: "#fff", border: "none", cursor: "pointer" }}>
            Guardar Cambios
          </button>
        </form>

        {/* Formulario Subir Foto */}
        <form onSubmit={handleUploadPhoto}>
          <div style={{ marginBottom: "15px" }}>
            <label>Subir Foto de Perfil:</label><br />
            <input type="file" onChange={(e) => setFotoFile(e.target.files[0])} />
          </div>
          <button type="submit" style={{ padding: "10px 20px", background: "#2ecc71", color: "#fff", border: "none", cursor: "pointer" }}>
            Subir Foto
          </button>
        </form>
      </div>
    </div>
  );
};

export default Perfil;
