import React, { useState, useEffect } from 'react';
import './tienda.css'; // Asegúrate de que el archivo existe en la misma carpeta

const Tienda = () => {
  const products = [
    { id: 1, name: "Camiseta Básica", category: "camisetas-hombre", price: 25, image: "👕", description: "Camiseta de algodón 100% cómoda y duradera" },
    { id: 2, name: "Camiseta Polo", category: "camisetas-hombre", price: 35, image: "👔", description: "Polo clásico perfecto para cualquier ocasión" },
    { id: 3, name: "Jeans Clásicos", category: "pantalones-hombre", price: 65, image: "👖", description: "Jeans de corte regular, perfectos para el día a día" },
    { id: 4, name: "Pantalón Chino", category: "pantalones-hombre", price: 55, image: "👔", description: "Pantalón elegante para look casual-formal" },
    { id: 5, name: "Zapatos Formales", category: "zapatos-hombre", price: 120, image: "👞", description: "Zapatos de cuero genuino para ocasiones especiales" },
    { id: 6, name: "Sneakers", category: "zapatos-hombre", price: 85, image: "👟", description: "Zapatillas cómodas para uso diario" },
    { id: 7, name: "Blusa Elegante", category: "blusas-mujer", price: 42, image: "👚", description: "Blusa de seda perfecta para la oficina" },
    { id: 8, name: "Top Casual", category: "blusas-mujer", price: 28, image: "👕", description: "Top cómodo para el día a día" },
    { id: 9, name: "Vestido de Noche", category: "vestidos-mujer", price: 89, image: "👗", description: "Vestido elegante para eventos especiales" },
    { id: 10, name: "Vestido Casual", category: "vestidos-mujer", price: 52, image: "👗", description: "Vestido cómodo para uso diario" },
    { id: 11, name: "Tacones Altos", category: "zapatos-mujer", price: 95, image: "👠", description: "Tacones elegantes para ocasiones especiales" },
    { id: 12, name: "Ballerinas", category: "zapatos-mujer", price: 68, image: "🥿", description: "Zapatos cómodos y versátiles" },
    { id: 13, name: "Bolso de Mano", category: "bolsos", price: 75, image: "👜", description: "Bolso elegante de cuero sintético" },
    { id: 14, name: "Cinturón de Cuero", category: "cinturones", price: 32, image: "🎀", description: "Cinturón clásico de cuero genuino" },
    { id: 15, name: "Camiseta Deportiva H", category: "deportivo-hombre", price: 38, image: "🏃‍♂️", description: "Camiseta técnica para deportes" },
    { id: 16, name: "Leggings Deportivos", category: "deportivo-mujer", price: 45, image: "🏃‍♀️", description: "Mallas deportivas de alta calidad" }
  ];

  const [currentFilter, setCurrentFilter] = useState('all');
  const [filteredProducts, setFilteredProducts] = useState(products);
  const [activeCategories, setActiveCategories] = useState({});
  const [activeSubcategory, setActiveSubcategory] = useState('');
  const [sectionTitle, setSectionTitle] = useState('Catálogo Completo');
  const [sectionDescription, setSectionDescription] = useState('Descubre nuestra amplia selección de productos');
  const [sortBy, setSortBy] = useState('name');
  const [sizeFilter, setSizeFilter] = useState('all');

  const categories = [
    {
      id: 'ropa-hombre',
      title: '👔 Ropa para Hombre',
      subcategories: [
        { id: 'camisetas-hombre', name: 'Camisetas' },
        { id: 'pantalones-hombre', name: 'Pantalones' },
        { id: 'chaquetas-hombre', name: 'Chaquetas' },
        { id: 'zapatos-hombre', name: 'Zapatos' }
      ]
    },
    {
      id: 'ropa-mujer',
      title: '👗 Ropa para Mujer',
      subcategories: [
        { id: 'blusas-mujer', name: 'Blusas' },
        { id: 'vestidos-mujer', name: 'Vestidos' },
        { id: 'pantalones-mujer', name: 'Pantalones' },
        { id: 'chaquetas-mujer', name: 'Chaquetas' },
        { id: 'zapatos-mujer', name: 'Zapatos' }
      ]
    },
    {
      id: 'accesorios',
      title: '💎 Accesorios',
      subcategories: [
        { id: 'bolsos', name: 'Bolsos' },
        { id: 'cinturones', name: 'Cinturones' },
        { id: 'joyeria', name: 'Joyería' },
        { id: 'gorras', name: 'Gorras y Sombreros' }
      ]
    },
    {
      id: 'deportivo',
      title: '🏃‍♂️ Ropa Deportiva',
      subcategories: [
        { id: 'deportivo-hombre', name: 'Hombre' },
        { id: 'deportivo-mujer', name: 'Mujer' },
        { id: 'zapatillas', name: 'Zapatillas' }
      ]
    }
  ];

  useEffect(() => {
    let filtered =
      currentFilter === 'all'
        ? [...products]
        : products.filter(product => product.category === currentFilter);

    switch (sortBy) {
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'newest':
        filtered.sort((a, b) => b.id - a.id);
        break;
      default:
        break;
    }

    setFilteredProducts(filtered);
}, [currentFilter, sortBy, products]); 

  const toggleCategory = (categoryId) => {
    setActiveCategories(prev => ({
      ...prev,
      [categoryId]: !prev[categoryId]
    }));
  };

  const handleSubcategoryClick = (subcategoryId, subcategoryName) => {
    setActiveSubcategory(subcategoryId);
    setCurrentFilter(subcategoryId);
    updateSectionTitle(subcategoryName, subcategoryId);
  };

  const updateSectionTitle = (subcategoryName, filter) => {
    if (filter.includes('hombre')) {
      setSectionTitle(`${subcategoryName} - Hombre`);
      setSectionDescription(`Descubre nuestra selección de ${subcategoryName.toLowerCase()} para hombre`);
    } else if (filter.includes('mujer')) {
      setSectionTitle(`${subcategoryName} - Mujer`);
      setSectionDescription(`Descubre nuestra selección de ${subcategoryName.toLowerCase()} para mujer`);
    } else {
      setSectionTitle(subcategoryName);
      setSectionDescription(`Explora nuestra colección de ${subcategoryName.toLowerCase()}`);
    }
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  const handleSizeChange = (e) => {
    setSizeFilter(e.target.value);
  };

  return (
    <div className="catalog-container">
      <nav className="sidebar">
        <div className="logo">
          <h1>Mi Tienda</h1>
        </div>

        {categories.map(category => (
          <div 
            key={category.id} 
            className={`category ${activeCategories[category.id] ? 'active' : ''}`}
          >
            <div 
              className="category-header"
              onClick={() => toggleCategory(category.id)}
            >
              <span>{category.title}</span>
              <span className="arrow">▼</span>
            </div>
            <div className="subcategories">
              {category.subcategories.map(subcategory => (
                <div
                  key={subcategory.id}
                  className={`subcategory ${activeSubcategory === subcategory.id ? 'active' : ''}`}
                  onClick={() => handleSubcategoryClick(subcategory.id, subcategory.name)}
                >
                  {subcategory.name}
                </div>
              ))}
            </div>
          </div>
        ))}
      </nav>

      <main className="main-content">
        <div className="header">
          <h2>{sectionTitle}</h2>
          <p>{sectionDescription}</p>
        </div>

        <div className="filter-bar">
          <span>Ordenar por:</span>
          <select value={sortBy} onChange={handleSortChange}>
            <option value="name">Nombre</option>
            <option value="price-low">Precio: Menor a Mayor</option>
            <option value="price-high">Precio: Mayor a Menor</option>
            <option value="newest">Más Recientes</option>
          </select>

          <span>Talla:</span>
          <select value={sizeFilter} onChange={handleSizeChange}>
            <option value="all">Todas las tallas</option>
            <option value="xs">XS</option>
            <option value="s">S</option>
            <option value="m">M</option>
            <option value="l">L</option>
            <option value="xl">XL</option>
            <option value="xxl">XXL</option>
          </select>
        </div>

        <div className="products-grid">
          {filteredProducts.length === 0 ? (
            <div className="no-products">
              <div className="no-products-icon">🛍️</div>
              <h3>No hay productos en esta categoría</h3>
              <p>Selecciona otra categoría para ver los productos disponibles</p>
            </div>
          ) : (
            filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <div className="product-image">{product.image}</div>
                <div className="product-info">
                  <div className="product-title">{product.name}</div>
                  <div className="product-description">{product.description}</div>
                  <div className="product-price">${product.price}</div>
                </div>
              </div>
            ))
          )}
        </div>
      </main>
    </div>
  );
};

export default Tienda;
