import React, { useState, useRef, useEffect } from 'react';
import { User, Camera, Mail, Save, X, Edit2, Check, AlertCircle } from 'lucide-react';

const UserProfileEditor = () => {
  // Estado del usuario
  const [user, setUser] = useState({
    nombre: '',
    correo: '',
    foto: null
  });

  // Estados para el modo de edición
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({ ...user });
  const [previewImage, setPreviewImage] = useState(null);
  
  // Estados para validación y feedback
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  // Ref para el input de archivo
  const fileInputRef = useRef(null);

  // Cargar datos del usuario al montar el componente
  useEffect(() => {
    // Aquí obtienes el email del usuario logueado
    // Cambia esto según cómo manejes la sesión
    const emailUsuario = localStorage.getItem('userEmail') || 'usuario@email.com';
    setUserEmail(emailUsuario);
    cargarDatosUsuario(emailUsuario);
  }, []);

  // Función para cargar datos del usuario desde el backend
  const cargarDatosUsuario = async (email) => {
    try {
      const response = await fetch(`http://localhost:3001/api/profile/${email}`);
      const data = await response.json();
      
      if (data.success) {
        const userData = {
          nombre: data.usuario.nombre,
          correo: data.usuario.correo,
          foto: data.usuario.foto
        };
        setUser(userData);
        setEditData(userData);
      } else {
        console.error('Error al cargar usuario:', data.mensaje);
      }
    } catch (error) {
      console.error('Error al conectar con el servidor:', error);
    }
  };

  // Validación de email
  const validateEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  // Validar formulario
  const validateForm = () => {
    const newErrors = {};
    
    if (!editData.nombre.trim()) {
      newErrors.nombre = 'El nombre es requerido';
    } else if (editData.nombre.trim().length < 2) {
      newErrors.nombre = 'El nombre debe tener al menos 2 caracteres';
    }
    
    if (!editData.correo.trim()) {
      newErrors.correo = 'El correo es requerido';
    } else if (!validateEmail(editData.correo)) {
      newErrors.correo = 'El correo no es válido';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Manejar cambio en inputs
  const handleInputChange = (field, value) => {
    setEditData(prev => ({ ...prev, [field]: value }));
    // Limpiar errores cuando el usuario empieza a corregir
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  // Manejar selección de imagen
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validar tipo de archivo
      if (!file.type.startsWith('image/')) {
        alert('Por favor selecciona un archivo de imagen válido');
        return;
      }
      
      // Validar tamaño (máximo 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('La imagen no puede superar los 5MB');
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
        setEditData(prev => ({ ...prev, foto: e.target.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  // Verificar disponibilidad de email
  const verificarEmail = async (email) => {
    if (email === user.correo) return true; // Email actual, no verificar
    
    try {
      const response = await fetch('http://localhost:3001/api/check-email', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          email: email,
          email_actual: user.correo 
        })
      });
      
      const data = await response.json();
      return data.disponible;
    } catch (error) {
      console.error('Error al verificar email:', error);
      return false;
    }
  };

  // Guardar cambios en el backend
  const handleSave = async () => {
    if (!validateForm()) return;

    // Verificar email si cambió
    if (editData.correo !== user.correo) {
      const emailDisponible = await verificarEmail(editData.correo);
      if (!emailDisponible) {
        setErrors(prev => ({ ...prev, correo: 'Este correo ya está en uso' }));
        return;
      }
    }

    setIsLoading(true);
    
    try {
      const response = await fetch('http://localhost:3001/api/profile', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email_actual: user.correo,
          nombre: editData.nombre,
          correo: editData.correo,
          foto: editData.foto
        })
      });
      
      const data = await response.json();
      
      if (data.success) {
        // Actualizar datos locales
        const newUserData = {
          nombre: data.usuario.nombre,
          correo: data.usuario.correo,
          foto: data.usuario.foto
        };
        
        setUser(newUserData);
        setEditData(newUserData);
        
        // Si cambió el email, actualizar localStorage
        if (data.usuario.correo !== userEmail) {
          localStorage.setItem('userEmail', data.usuario.correo);
          setUserEmail(data.usuario.correo);
        }
        
        setIsEditing(false);
        setPreviewImage(null);
        setShowSuccess(true);
        
        // Ocultar mensaje de éxito después de 3 segundos
        setTimeout(() => setShowSuccess(false), 3000);
        
      } else {
        alert(`Error: ${data.mensaje}`);
      }
      
    } catch (error) {
      console.error('Error al guardar:', error);
      alert('Error al conectar con el servidor. Inténtalo de nuevo.');
    } finally {
      setIsLoading(false);
    }
  };

  // Cancelar edición
  const handleCancel = () => {
    setEditData({ ...user });
    setPreviewImage(null);
    setErrors({});
    setIsEditing(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-2xl mx-auto">
        {/* Mensaje de éxito */}
        {showSuccess && (
          <div className="mb-6 bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-lg flex items-center">
            <Check className="w-5 h-5 mr-2" />
            ¡Perfil actualizado correctamente!
          </div>
        )}

        {/* Tarjeta principal */}
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-indigo-600 px-8 py-6">
            <div className="flex justify-between items-center">
              <h1 className="text-3xl font-bold text-white">Mi Perfil</h1>
              {!isEditing && (
                <button
                  onClick={() => setIsEditing(true)}
                  className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white px-4 py-2 rounded-lg flex items-center transition-all duration-200"
                >
                  <Edit2 className="w-4 h-4 mr-2" />
                  Editar
                </button>
              )}
            </div>
          </div>

          {/* Contenido */}
          <div className="p-8">
            <div className="flex flex-col lg:flex-row lg:items-start lg:space-x-8">
              {/* Sección de avatar */}
              <div className="flex flex-col items-center mb-8 lg:mb-0">
                <div className="relative group">
                  <div className="w-32 h-32 rounded-full overflow-hidden bg-gray-200 border-4 border-white shadow-lg">
                    {(isEditing ? previewImage || editData.foto : user.foto) ? (
                      <img
                        src={isEditing ? previewImage || editData.foto : user.foto}
                        alt="Avatar"
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center bg-gray-300">
                        <User className="w-16 h-16 text-gray-500" />
                      </div>
                    )}
                  </div>
                  
                  {isEditing && (
                    <button
                      onClick={() => fileInputRef.current?.click()}
                      className="absolute inset-0 bg-black bg-opacity-50 rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-200"
                    >
                      <Camera className="w-8 h-8 text-white" />
                    </button>
                  )}
                </div>
                
                {isEditing && (
                  <button
                    onClick={() => fileInputRef.current?.click()}
                    className="mt-3 text-blue-600 hover:text-blue-800 text-sm font-medium flex items-center"
                  >
                    <Camera className="w-4 h-4 mr-1" />
                    Cambiar foto
                  </button>
                )}

                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleImageChange}
                  className="hidden"
                />
              </div>

              {/* Formulario de datos */}
              <div className="flex-1 space-y-6">
                {/* Campo Nombre */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Nombre completo
                  </label>
                  {isEditing ? (
                    <div>
                      <input
                        type="text"
                        value={editData.nombre}
                        onChange={(e) => handleInputChange('nombre', e.target.value)}
                        className={`w-full px-4 py-3 border-2 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${
                          errors.nombre ? 'border-red-300' : 'border-gray-200'
                        }`}
                        placeholder="Ingresa tu nombre completo"
                      />
                      {errors.nombre && (
                        <p className="mt-1 text-sm text-red-600 flex items-center">
                          <AlertCircle className="w-4 h-4 mr-1" />
                          {errors.nombre}
                        </p>
                      )}
                    </div>
                  ) : (
                    <div className="flex items-center px-4 py-3 bg-gray-50 rounded-lg">
                      <User className="w-5 h-5 text-gray-400 mr-3" />
                      <span className="text-gray-900 font-medium">{user.nombre}</span>
                    </div>
                  )}
                </div>

                {/* Campo Email */}
                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Correo electrónico
                  </label>
                  {isEditing ? (
                    <div>
                      <input
                        type="email"
                        value={editData.correo}
                        onChange={(e) => handleInputChange('correo', e.target.value)}
                        className={`w-full px-4 py-3 border-2 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all ${
                          errors.correo ? 'border-red-300' : 'border-gray-200'
                        }`}
                        placeholder="Ingresa tu correo electrónico"
                      />
                      {errors.correo && (
                        <p className="mt-1 text-sm text-red-600 flex items-center">
                          <AlertCircle className="w-4 h-4 mr-1" />
                          {errors.correo}
                        </p>
                      )}
                    </div>
                  ) : (
                    <div className="flex items-center px-4 py-3 bg-gray-50 rounded-lg">
                      <Mail className="w-5 h-5 text-gray-400 mr-3" />
                      <span className="text-gray-900 font-medium">{user.correo}</span>
                    </div>
                  )}
                </div>

                {/* Botones de acción */}
                {isEditing && (
                  <div className="flex flex-col sm:flex-row gap-4 pt-6">
                    <button
                      onClick={handleSave}
                      disabled={isLoading}
                      className="flex-1 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white px-6 py-3 rounded-lg font-semibold flex items-center justify-center transition-all duration-200"
                    >
                      {isLoading ? (
                        <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                      ) : (
                        <Save className="w-5 h-5 mr-2" />
                      )}
                      {isLoading ? 'Guardando...' : 'Guardar cambios'}
                    </button>
                    
                    <button
                      onClick={handleCancel}
                      disabled={isLoading}
                      className="flex-1 bg-gray-500 hover:bg-gray-600 disabled:bg-gray-400 text-white px-6 py-3 rounded-lg font-semibold flex items-center justify-center transition-all duration-200"
                    >
                      <X className="w-5 h-5 mr-2" />
                      Cancelar
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Información adicional */}
        <div className="mt-6 bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-3">Información importante</h3>
          <ul className="text-sm text-gray-600 space-y-2">
            <li>• Las imágenes deben ser menores a 5MB</li>
            <li>• El email debe ser válido y único en el sistema</li>
            <li>• Los cambios se guardan en tu base de datos PostgreSQL</li>
            <li>• Si cambias tu email, deberás usarlo para el próximo login</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default UserProfileEditor;
