import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./perfil.css";

const API_URL = "http://localhost:5000"; // Backend puerto 5000

const Perfil = () => {
  const [user, setUser] = useState({ nombre: "", correo: "", foto: "" });
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");
  const [fotoFile, setFotoFile] = useState(null);
  const [mensaje, setMensaje] = useState("");

  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // Cargar perfil al iniciar
  useEffect(() => {
    if (!token) {
      setMensaje("❌ No has iniciado sesión");
      return;
    }

    axios
      .get(`${API_URL}/api/profile`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        console.log("Perfil API:", res.data);
        setUser(res.data);
        setNombre(res.data.nombre);
        setCorreo(res.data.correo);
      })
      .catch((err) => {
        console.error("Error al cargar perfil:", err);
        setMensaje("❌ Error al cargar perfil, revisa tu sesión");
        // Si el token es inválido, eliminarlo y redirigir al login
        localStorage.removeItem("token");
        navigate("/login");
      });
  }, [token, navigate]);

  // Actualizar nombre y correo
  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    if (!nombre || !correo) {
      setMensaje("❌ Nombre y correo son obligatorios");
      return;
    }

    try {
      const res = await axios.put(
        `${API_URL}/api/profile`,
        { nombre, correo },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser({ ...user, nombre, correo });
      setMensaje("✅ Perfil actualizado correctamente");
    } catch (err) {
      console.error("Error al actualizar perfil:", err);
      setMensaje("❌ Error al actualizar perfil");
    }
  };

  // Subir foto
  const handleUploadPhoto = async (e) => {
    e.preventDefault();
    if (!fotoFile) {
      setMensaje("❌ Selecciona una foto primero");
      return;
    }

    const formData = new FormData();
    formData.append("foto", fotoFile); // "foto" coincide con multer

    try {
      const res = await axios.post(`${API_URL}/api/profile/photo`, formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      setUser({ ...user, foto: res.data.foto });
      setMensaje("✅ Foto actualizada correctamente");
    } catch (err) {
      console.error("Error al subir foto:", err);
      setMensaje("❌ Error al subir foto");
    }
  };

  return (
    <div className="perfil-container">
      {/* Sidebar */}
      <div className="perfil-sidebar">
        <h2>PERFIL</h2>
        <button
          className="nav-home-btn"
          onClick={() => navigate("/home")}
        >
          Home
        </button>
        <img
          src={user.foto ? `${API_URL}${user.foto}` : "/default-avatar.png"}
          alt="Foto de perfil"
        />
        <p><strong>Nombre:</strong> {user.nombre}</p>
        <p><strong>Correo:</strong> {user.correo}</p>
      </div>

      {/* Main */}
      <div className="perfil-main">
        <h2>Editar Perfil</h2>
        {mensaje && <p className="mensaje">{mensaje}</p>}

        <form className="perfil-form" onSubmit={handleUpdateProfile}>
          <label>Nombre:</label>
          <input
            type="text"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />

          <label>Correo:</label>
          <input
            type="email"
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
          />

          <button type="submit" className="guardar">
            Guardar Cambios
          </button>
        </form>

        <form className="perfil-form" onSubmit={handleUploadPhoto}>
          <label>Subir Foto de Perfil:</label>
          <input
            type="file"
            name="foto"
            accept="image/*"
            onChange={(e) => setFotoFile(e.target.files[0])}
          />
          <button type="submit" className="subir">
            Subir Foto
          </button>
        </form>
      </div>
    </div>
  );
};

export default Perfil;
