import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function DashboardEncargado() {   
  const [novedades, setNovedades] = useState([]);
  const [titulo, setTitulo] = useState("");
  const [imagen, setImagen] = useState(null);

  const handleAddNovedad = (e) => {
    e.preventDefault();
    if (!titulo || !imagen) {
      alert("Por favor ingresa un título y una imagen");
      return;
    }
    const nueva = { titulo, imagen: URL.createObjectURL(imagen) };
    setNovedades([...novedades, nueva]);
    setTitulo("");
    setImagen(null);
  };

  return (
    <div className="encargado-container">
      <h2 className="mb-4 text-center">Dashboard Encargado</h2>

      {/* Sección novedades */}
      <div id="novedades" className="card p-4 mb-4">
        <h4 className="mb-3">Añadir Novedad</h4>
        <form onSubmit={handleAddNovedad}>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Título de la novedad"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <input
              type="file"
              className="form-control"
              accept="image/*"
              onChange={(e) => setImagen(e.target.files[0])}
            />
          </div>
          <button type="submit" className="btn btn-primary w-100">
            Añadir Novedad
          </button>
        </form>
      </div>

      {/* Listado de novedades */}
      <div className="row">
        {novedades.map((nov, idx) => (
          <div key={idx} className="col-md-4 mb-3">
            <div className="card novedad-card">
              <img src={nov.imagen} className="card-img-top" alt={nov.titulo} />
              <div className="card-body">
                <h5 className="card-title">{nov.titulo}</h5>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DashboardEncargado;
