import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mensaje, setMensaje] = useState("");
  const [esError, setEsError] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMensaje(`‚úÖ Bienvenido, ${data.user?.nombre || email}`);
        setEsError(false);

        // üîπ Guardamos datos de sesi√≥n
        if (data.token) localStorage.setItem("token", data.token);
        if (data.user) {
          localStorage.setItem("user", JSON.stringify(data.user));
        } else {
          localStorage.setItem("user", JSON.stringify({ email }));
        }

        if (setIsLoggedIn) setIsLoggedIn(true);

        // üîπ Redirecci√≥n seg√∫n rol
        if (data.rol === "admin") {
          navigate("/dashboard", {
            state: { mensaje: `Bienvenido, ${data.user?.nombre || email}` },
          });
        } else {
          navigate("/home", {
            state: { mensaje: `Bienvenido, ${data.user?.nombre || email}` },
          });
        }
      } else {
        setMensaje("‚ùå Usuario o contrase√±a incorrectos");
        setEsError(true);
      }
    } catch (error) {
      console.error("Error al iniciar sesi√≥n:", error);
      setMensaje("‚ùå Error del servidor o conexi√≥n fallida");
      setEsError(true);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>HYPE DISTRICT</h2>
        <form id="login-form" onSubmit={handleSubmit}>
          <div className="input-box">
            <label htmlFor="email">Correo electr√≥nico</label>
            <input
              type="email"
              id="email"
              name="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className="input-box">
            <label htmlFor="password">Contrase√±a</label>
            <input
              type="password"
              id="password"
              name="password"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button type="submit" className="btn-login">
            Iniciar sesi√≥n
          </button>
        </form>

        {mensaje && (
          <div
            id="mensaje"
            className="signup-link"
            style={{
              color: esError ? "#ff4d4d" : "#0ffff8",
              marginTop: "10px",
            }}
          >
            {mensaje}
          </div>
        )}

        <div className="signup-link" style={{ marginTop: "15px" }}>
          <button
            onClick={() => navigate("/forgot-password")}
            className="btn-olvidaste"
          >
            ¬øOlvidaste tu contrase√±a?
          </button>
        </div>

        <div className="signup-link" style={{ marginTop: "20px" }}>
          ¬øNo tienes cuenta? <br />
          <button onClick={() => navigate("/registrarse")} className="btn-registrarse">
            Reg√≠strate aqu√≠
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
