import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './home.css';

const Home = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const [mensaje, setMensaje] = useState("");
  const [visible, setVisible] = useState(true);
  const [showGoodbye, setShowGoodbye] = useState(false);

  useEffect(() => {
    // Obtener nombre o correo desde localStorage
    const nombreCliente = localStorage.getItem("nombreCliente");
    const correoCliente = localStorage.getItem("correoCliente");

    if (nombreCliente) {
      setMensaje(`¬°Hola, ${nombreCliente}! Bienvenido de vuelta üòé`);
    } else if (correoCliente) {
      setMensaje(`¬°Bienvenido de nuevo, ${correoCliente}!`);
    } else {
      setMensaje("¬°Bienvenido a HYPE DISTRICT, tu estilo comienza aqu√≠!");
    }
  }, []);

  useEffect(() => {
    if (mensaje) {
      const timer1 = setTimeout(() => {
        setVisible(false);
      }, 9000);

      const timer2 = setTimeout(() => {
        setMensaje("");
      }, 2500);

      return () => {
        clearTimeout(timer1);
        clearTimeout(timer2);
      };
    }
  }, [mensaje]);

  const handleCerrarSesion = () => {
    setShowGoodbye(true);
    setTimeout(() => {
      // Limpia datos del usuario si deseas
      localStorage.removeItem("nombreCliente");
      localStorage.removeItem("correoCliente");
      navigate('/');
    }, 2000);
  };

  return (
    <div className="home-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="logo" onClick={() => navigate("/home")}>
          HYPE DISTRICT
        </div>
        <ul className="nav-links">
          <li onClick={() => navigate("/home")}>Inicio</li>
          <li onClick={handleCerrarSesion}>Cerrar Sesi√≥n</li>
          <li onClick={() => navigate("/contactanos")}>Cont√°ctanos</li> 
          <li onClick={() => navigate("/carrito")}>üõí</li>         
        </ul>
      </nav>

      {/* Mensaje de bienvenida */}
      {mensaje && (
        <div className={`mensaje-bienvenida ${visible ? "fade-in" : "fade-out"}`}>
          {mensaje}
        </div>
      )}

      {/* Contenido principal */}
      <div className="hero" style={{ marginTop: "120px" }}>
        <h1>HYPE DISTRICT</h1>
        <p>La cultura urbana se vive aqu√≠. Encuentra tu estilo, tus zapatillas, tu identidad.</p>

        {/* Mensaje despedida */}
        {showGoodbye && (
          <div className="goodbye-message">
            ¬°Gracias por visitarnos! Hasta pronto üëã
          </div>
        )}
      </div>

      <div className="destacados">
        <div className="card">
          <h3>üî• Zapatillas Exclusivas</h3>
          <p>Modelos √∫nicos y coleccionables</p>
        </div>
        <div className="card">
          <h3>üéß Estilo Urbano</h3>
          <p>Ropa y accesorios con flow callejero</p>
        </div>
        <div className="card">
          <h3>üß¢ Comunidad</h3>
          <p>√önete a miles que viven el hype</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
