import React, { useState } from "react";
import "./contact.css";

const Contact = () => {
  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    mensaje: "",
    foto: null,
  });
  const [respuesta, setRespuesta] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "foto") {
      setFormData({ ...formData, foto: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const dataToSend = new FormData();
    dataToSend.append("nombre", formData.nombre);
    dataToSend.append("email", formData.email);
    dataToSend.append("mensaje", formData.mensaje);
    if (formData.foto) {
      dataToSend.append("foto", formData.foto);
    }

    const res = await fetch("http://localhost:3000/api/contacto", {
      method: "POST",
      body: dataToSend,
    });

    const data = await res.json();
    setRespuesta(data.mensaje);

    // Reiniciar formulario
    setFormData({
      nombre: "",
      email: "",
      mensaje: "",
      foto: null,
    });
  };

  return (
    <div className="contact-container">
        <h1 className="titulo-contacto">CONTACTA CON NOSOTROS</h1>
        <p className="subtitulo-contacto">Escribe aqu√≠ dudas o sugerencias</p>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input
          type="text"
          name="nombre"
          placeholder="Tu nombre"
          value={formData.nombre}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Tu correo"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <textarea
          name="mensaje"
          placeholder="Escribe tu mensaje"
          value={formData.mensaje}
          onChange={handleChange}
          required
        />
        <input
          type="file"
          name="foto"
          accept="image/*"
          onChange={handleChange}
        />
        <button type="submit">Enviar</button>
      </form>
      {respuesta && <p>{respuesta}</p>}
    </div>
  );
};

export default Contact;
