          <h3>ðŸ§¢ Comunidad</h3>
          <p>Ãšnete a miles que viven el hype</p>
        </div>
      </div>

      {/* Carrito Modal Animado */}
      <div className={`carrito-modal ${mostrarCarrito ? 'activo' : ''}`}>
        <div className="carrito-contenido">
          <span className="cerrar" onClick={() => setMostrarCarrito(false)}>âœ–</span>
          <h2>ðŸ›’ Tu Carrito</h2>
          {productosEjemplo.map((item) => (
            <div key={item.id} className="item-carrito">
              <p>{item.nombre}</p>
