import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './Login.css';

const Login = ({ setIsLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mensaje, setMensaje] = useState("");
  const [esError, setEsError] = useState(false);
  const navigate = useNavigate();

  // Usuario hardcodeado
  const usuarioFijo = {
    email: "usuario@hypedistrict.com",
    password: "123456",
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (email === usuarioFijo.email && password === usuarioFijo.password) {
      setMensaje(`✅ Bienvenido, ${email}`);
      setEsError(false);
      if (setIsLoggedIn) {
        setIsLoggedIn(true);
      }
      navigate("/dashboard");
    } else {
      setMensaje("❌ Usuario o contraseña incorrectos");
      setEsError(true);
    }
  };

  const irARegistro = () => {
    navigate("/registrarse");
  };

  return (
    <div className="login-box">
      <h2>HYPE DISTRICT</h2>
      <form id="login-form" onSubmit={handleSubmit}>
        <div className="input-box">
          <label htmlFor="email">Correo electrónico</label>
          <input
            type="email"
            id="email"
            name="email"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-box">
          <label htmlFor="password">Contraseña</label>
          <input
            type="password"
            id="password"
            name="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn-login">
          Iniciar sesión
        </button>
      </form>

      {mensaje && (
        <div
          id="mensaje"
          className="signup-link"
          style={{ color: esError ? "#ff4d4d" : "#0ffff8", marginTop: "10px" }}
        >
          {mensaje}
        </div>
      )}

      <div className="signup-link" style={{ marginTop: "10px" }}>
        ¿No tienes cuenta?{" "}
        <button onClick={irARegistro} className="btn-link">
          Regístrate aquí
        </button>
      </div>
    </div>
  );
};

export default Login;
