import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './perfil.css';

const Perfil = () => {
  const [user, setUser] = useState({});
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const token = localStorage.getItem('token'); // guardas token al iniciar sesión

  useEffect(() => {
    axios.get('http://localhost:6000/api/profile', {
      headers: { Authorization: token }
    }).then(res => {
      setUser(res.data);
      setNombre(res.data.nombre);
      setCorreo(res.data.correo);
    });
  }, []);

  const actualizarPerfil = () => {
    axios.put('http://localhost:6000/api/profile', { nombre, correo }, {
      headers: { Authorization: token }
    }).then(res => alert(res.data.msg));
  };

  const subirFoto = (e) => {
    const formData = new FormData();
    formData.append('foto', e.target.files[0]);

    axios.post('http://localhost:6000/api/profile/photo', formData, {
      headers: { Authorization: token, 'Content-Type': 'multipart/form-data' }
    }).then(res => {
      setUser(prev => ({ ...prev, foto: res.data.foto }));
    });
  };

  return (
    <div className="perfil-container">
      <div className="sidebar">
        <h2>Menú</h2>
        <ul>
          <li>Perfil</li>
          <li>Configuración</li>
          <li>Cerrar Sesión</li>
        </ul>
      </div>
      <div className="perfil-content">
        <div className="foto-section">
          <img src={user.foto || '/default-avatar.png'} alt="Perfil" />
          <input type="file" onChange={subirFoto} />
        </div>
        <div className="info-section">
          <label>Nombre</label>
          <input value={nombre} onChange={e => setNombre(e.target.value)} />
          <label>Correo</label>
          <input value={correo} onChange={e => setCorreo(e.target.value)} />
          <button onClick={actualizarPerfil}>Guardar</button>
        </div>
      </div>
    </div>
  );
};

export default Perfil;
