import React, { useState } from 'react';



const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const menuItems = [
    { id: 'dashboard', name: 'Dashboard', icon: '' },
    { id: 'products', name: 'Productos', icon: '' },
    { id: 'users', name: 'Usuarios', icon: '' },
    { id: 'sales', name: 'Ventas', icon: '' },
    { id: 'settings', name: 'Configuraci贸n', icon: '锔' }
  ];

  const statsData = [
    { title: 'Total Usuarios', value: '1,234', change: '+12%', color: '#60a5fa', bgColor: '#1e3a8a' },
    { title: 'Ventas Totales', value: '$45,678', change: '+8%', color: '#4ade80', bgColor: '#14532d' },
    { title: 'Productos', value: '567', change: '+3%', color: '#a78bfa', bgColor: '#581c87' },
    { title: 'Pedidos', value: '89', change: '+15%', color: '#fbbf24', bgColor: '#78350f' }
  ];

  const recentProducts = [
    { id: 1, name: 'Camiseta Urbana', price: '$20', stock: 45, status: 'Disponible' },
    { id: 2, name: 'Camiseta Oversize', price: '$30', stock: 23, status: 'Disponible' },
    { id: 3, name: 'Gorra Negra', price: '$15', stock: 12, status: 'Bajo Stock' },
    { id: 4, name: 'Chaqueta Premium', price: '$45', stock: 8, status: 'Bajo Stock' },
    { id: 5, name: 'Pantal贸n Casual', price: '$35', stock: 0, status: 'Agotado' }
  ];

  const getStatusColor = (status) => {
    switch(status) {
      case 'Disponible': return { color: '#4ade80', backgroundColor: '#14532d' };
      case 'Bajo Stock': return { color: '#fbbf24', backgroundColor: '#78350f' };
      case 'Agotado': return { color: '#f87171', backgroundColor: '#7f1d1d' };
      default: return { color: '#9ca3af', backgroundColor: '#374151' };
    }
  };

  const styles = {
    container: {
      minHeight: '100vh',
      backgroundColor: '#111827',
      color: 'white',
      fontFamily: 'Arial, sans-serif'
    },
    backdrop: {
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      zIndex: 40,
      display: sidebarOpen ? 'block' : 'none'
    },
    sidebar: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '256px',
      height: '100vh',
      backgroundColor: '#1f2937',
      transform: sidebarOpen ? 'translateX(0)' : 'translateX(-100%)',
      transition: 'transform 0.3s ease-in-out',
      zIndex: 50
    },
    sidebarDesktop: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '256px',
      height: '100vh',
      backgroundColor: '#1f2937',
      transform: 'translateX(0)',
      zIndex: 50
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      height: '64px',
      backgroundColor: '#111827',
      borderBottom: '1px solid #374151',
      fontSize: '20px',
      fontWeight: 'bold'
    },
    nav: {
      marginTop: '32px',
      padding: '0 16px'
    },
    navButton: {
      width: '100%',
      display: 'flex',
      alignItems: 'center',
      padding: '12px 16px',
      textAlign: 'left',
      borderRadius: '8px',
      border: 'none',
      marginBottom: '8px',
      cursor: 'pointer',
      transition: 'background-color 0.2s',
      fontSize: '14px',
      fontWeight: '500'
    },
    navButtonActive: {
      backgroundColor: '#2563eb',
      color: 'white'
    },
    navButtonInactive: {
      backgroundColor: 'transparent',
      color: '#d1d5db'
    },
    userProfile: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      right: 0,
      padding: '16px',
      borderTop: '1px solid #374151',
      display: 'flex',
      alignItems: 'center'
    },
    userAvatar: {
      width: '40px',
      height: '40px',
      backgroundColor: '#2563eb',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 'bold',
      marginRight: '12px'
    },
    mainContent: {
      marginLeft: '256px',
      minHeight: '100vh'
    },
    mainContentMobile: {
      marginLeft: '0',
      minHeight: '100vh'
    },
    header: {
      backgroundColor: '#111827',
      borderBottom: '1px solid #374151',
      position: 'sticky',
      top: 0,
      zIndex: 30,
      padding: '0 24px'
    },
    headerContent: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      height: '64px'
    },
    menuButton: {
      color: '#9ca3af',
      backgroundColor: 'transparent',
      border: 'none',
      fontSize: '20px',
      cursor: 'pointer',
      display: 'block'
    },
    menuButtonDesktop: {
      display: 'none'
    },
    pageTitle: {
      fontSize: '20px',
      fontWeight: '600',
      textTransform: 'capitalize',
      flex: 1
    },
    headerActions: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px'
    },
    notificationButton: {
      position: 'relative',
      backgroundColor: 'transparent',
      border: 'none',
      color: '#9ca3af',
      fontSize: '20px',
      cursor: 'pointer'
    },
    notificationBadge: {
      position: 'absolute',
      top: '-4px',
      right: '-4px',
      width: '12px',
      height: '12px',
      backgroundColor: '#ef4444',
      borderRadius: '50%'
    },
    logoutButton: {
      backgroundColor: '#2563eb',
      color: 'white',
      border: 'none',
      padding: '8px 16px',
      borderRadius: '8px',
      fontSize: '14px',
      fontWeight: '500',
      cursor: 'pointer',
      transition: 'background-color 0.2s'
    },
    main: {
      padding: '32px'
    },
    alert: {
      marginBottom: '24px',
      backgroundColor: '#78350f',
      border: '1px solid #a16207',
      color: '#fcd34d',
      padding: '12px 16px',
      borderRadius: '8px',
      display: 'flex',
      alignItems: 'center'
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '24px',
      marginBottom: '32px'
    },
    statCard: {
      backgroundColor: '#1f2937',
      borderRadius: '8px',
      padding: '24px',
      border: '1px solid #374151'
    },
    statContent: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      marginBottom: '16px'
    },
    statIcon: {
      padding: '12px',
      borderRadius: '8px',
      fontSize: '20px'
    },
    statValue: {
      fontSize: '24px',
      fontWeight: 'bold',
      color: 'white',
      marginTop: '4px'
    },
    statTitle: {
      fontSize: '14px',
      color: '#9ca3af',
      marginBottom: '4px'
    },
    statChange: {
      display: 'flex',
      alignItems: 'center',
      fontSize: '14px'
    },
    contentGrid: {
      display: 'grid',
      gridTemplateColumns: '2fr 1fr',
      gap: '24px'
    },
    contentGridMobile: {
      display: 'grid',
      gridTemplateColumns: '1fr',
      gap: '24px'
    },
    card: {
      backgroundColor: '#1f2937',
      borderRadius: '8px',
      border: '1px solid #374151'
    },
    cardHeader: {
      padding: '24px',
      borderBottom: '1px solid #374151',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    cardTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: 'white'
    },
    viewAllButton: {
      color: '#60a5fa',
      backgroundColor: 'transparent',
      border: 'none',
      fontSize: '14px',
      fontWeight: '500',
      cursor: 'pointer'
    },
    table: {
      width: '100%',
      borderCollapse: 'collapse'
    },
    tableHeader: {
      textAlign: 'left',
      padding: '16px',
      fontSize: '14px',
      fontWeight: '500',
      color: '#9ca3af',
      borderBottom: '1px solid #374151'
    },
    tableRow: {
      borderBottom: '1px solid #374151'
    },
    tableCell: {
      padding: '16px',
      color: '#d1d5db'
    },
    tableCellBold: {
      padding: '16px',
      color: 'white',
      fontWeight: '500'
    },
    statusBadge: {
      padding: '4px 8px',
      borderRadius: '9999px',
      fontSize: '12px',
      fontWeight: '500'
    },
    rightColumn: {
      display: 'flex',
      flexDirection: 'column',
      gap: '24px'
    },
    actionButton: {
      width: '100%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '12px',
      borderRadius: '8px',
      border: 'none',
      color: 'white',
      fontSize: '14px',
      fontWeight: '500',
      cursor: 'pointer',
      transition: 'background-color 0.2s',
      marginBottom: '12px'
    },
    activityItem: {
      display: 'flex',
      alignItems: 'center',
      fontSize: '14px',
      marginBottom: '12px'
    }
  };

  const handleNavButtonHover = (e, isActive) => {
    if (!isActive) {
      e.target.style.backgroundColor = '#374151';
    }
  };

  const handleNavButtonLeave = (e, isActive) => {
    if (!isActive) {
      e.target.style.backgroundColor = 'transparent';
    }
  };

  const handleLogoutHover = (e) => {
    e.target.style.backgroundColor = '#1d4ed8';
  };

  const handleLogoutLeave = (e) => {
    e.target.style.backgroundColor = '#2563eb';
  };

  return (
    <div style={styles.container}>
      {/* Mobile sidebar backdrop */}
      <div style={styles.backdrop} onClick={() => setSidebarOpen(false)} />

      {/* Sidebar */}
      <div style={window.innerWidth >= 1024 ? styles.sidebarDesktop : styles.sidebar}>
        {/* Logo */}
        <div style={styles.logo}>
          <span style={{ fontSize: '24px', marginRight: '8px' }}></span>
          <span>Mi Dashboard</span>
        </div>

        {/* Navigation */}
        <nav style={styles.nav}>
          {menuItems.map((item) => (
            <button
              key={item.id}
              onClick={() => {
                setActiveTab(item.id);
                setSidebarOpen(false);
              }}
              style={{
                ...styles.navButton,
                ...(activeTab === item.id ? styles.navButtonActive : styles.navButtonInactive)
              }}
              onMouseEnter={(e) => handleNavButtonHover(e, activeTab === item.id)}
              onMouseLeave={(e) => handleNavButtonLeave(e, activeTab === item.id)}
            >
              <span style={{ fontSize: '20px', marginRight: '12px' }}>{item.icon}</span>
              <span>{item.name}</span>
            </button>
          ))}
        </nav>

        {/* User Profile */}
        <div style={styles.userProfile}>
          <div style={styles.userAvatar}>
            <span>U</span>
          </div>
          <div>
            <p style={{ fontSize: '14px', fontWeight: '500', margin: 0 }}>Usuario Admin</p>
            <p style={{ fontSize: '12px', color: '#9ca3af', margin: 0 }}>admin@empresa.com</p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={window.innerWidth >= 1024 ? styles.mainContent : styles.mainContentMobile}>
        {/* Header */}
        <header style={styles.header}>
          <div style={styles.headerContent}>
            {/* Mobile menu button */}
            <button
              onClick={() => setSidebarOpen(true)}
              style={window.innerWidth >= 1024 ? styles.menuButtonDesktop : styles.menuButton}
            >
              <span></span>
            </button>

            {/* Page title */}
            <h1 style={styles.pageTitle}>
              {activeTab === 'dashboard' ? 'Panel Principal' : activeTab}
            </h1>

            {/* Header actions */}
            <div style={styles.headerActions}>
              <button style={styles.notificationButton}>
                <span></span>
                <span style={styles.notificationBadge}></span>
              </button>
              <button 
                style={styles.logoutButton}
                onMouseEnter={handleLogoutHover}
                onMouseLeave={handleLogoutLeave}
              >
                Cerrar Sesi贸n
              </button>
            </div>
          </div>
        </header>

        {/* Page Content */}
        <main style={styles.main}>
          {/* Alert */}
          <div style={styles.alert}>
            <span style={{ fontSize: '20px', marginRight: '8px' }}>锔</span>
            <span>隆Atenci贸n! Hay nuevas tareas pendientes por revisar.</span>
          </div>

          {/* Stats Grid */}
          <div style={styles.statsGrid}>
            {statsData.map((stat, index) => (
              <div key={index} style={styles.statCard}>
                <div style={styles.statContent}>
                  <div>
                    <p style={styles.statTitle}>{stat.title}</p>
                    <p style={styles.statValue}>{stat.value}</p>
                  </div>
                  <div style={{ ...styles.statIcon, backgroundColor: stat.bgColor, color: stat.color }}>
                    <span></span>
                  </div>
                </div>
                <div style={styles.statChange}>
                  <span style={{ color: '#4ade80', fontWeight: '500' }}>{stat.change}</span>
                  <span style={{ color: '#9ca3af', marginLeft: '8px' }}>vs 煤ltimo mes</span>
                </div>
              </div>
            ))}
          </div>

          {/* Content Grid */}
          <div style={window.innerWidth >= 1024 ? styles.contentGrid : styles.contentGridMobile}>
            {/* Recent Products */}
            <div style={styles.card}>
              <div style={styles.cardHeader}>
                <h2 style={styles.cardTitle}>Productos Recientes</h2>
                <button style={styles.viewAllButton}>Ver todos</button>
              </div>
              <div style={{ overflowX: 'auto' }}>
                <table style={styles.table}>
                  <thead>
                    <tr>
                      <th style={styles.tableHeader}>Producto</th>
                      <th style={styles.tableHeader}>Precio</th>
                      <th style={styles.tableHeader}>Stock</th>
                      <th style={styles.tableHeader}>Estado</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recentProducts.map((product) => (
                      <tr key={product.id} style={styles.tableRow}>
                        <td style={styles.tableCellBold}>{product.name}</td>
                        <td style={styles.tableCell}>{product.price}</td>
                        <td style={styles.tableCell}>{product.stock}</td>
                        <td style={styles.tableCell}>
                          <span style={{ 
                            ...styles.statusBadge, 
                            ...getStatusColor(product.status)
                          }}>
                            {product.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Quick Actions */}
            <div style={styles.rightColumn}>
              <div style={styles.card}>
                <div style={{ padding: '24px' }}>
                  <h3 style={styles.cardTitle}>Acciones R谩pidas</h3>
                  <div style={{ marginTop: '16px' }}>
                    <button style={{ ...styles.actionButton, backgroundColor: '#2563eb' }}>
                      <span style={{ marginRight: '8px' }}></span>
                      Agregar Producto
                    </button>
                    <button style={{ ...styles.actionButton, backgroundColor: '#059669' }}>
                      <span style={{ marginRight: '8px' }}></span>
                      Nuevo Usuario
                    </button>
                    <button style={{ ...styles.actionButton, backgroundColor: '#7c3aed' }}>
                      <span style={{ marginRight: '8px' }}></span>
                      Ver Reportes
                    </button>
                  </div>
                </div>
              </div>

              <div style={styles.card}>
                <div style={{ padding: '24px' }}>
                  <h3 style={styles.cardTitle}>Actividad Reciente</h3>
                  <div style={{ marginTop: '16px' }}>
                    <div style={styles.activityItem}>
                      <span style={{ color: '#4ade80', marginRight: '8px' }}></span>
                      <span style={{ color: '#d1d5db' }}>Producto agregado</span>
                    </div>
                    <div style={styles.activityItem}>
                      <span style={{ color: '#60a5fa', marginRight: '8px' }}></span>
                      <span style={{ color: '#d1d5db' }}>Usuario actualizado</span>
                    </div>
                    <div style={styles.activityItem}>
                      <span style={{ color: '#fbbf24', marginRight: '8px' }}>锔</span>
                      <span style={{ color: '#d1d5db' }}>Stock bajo detectado</span>
                    </div>
                    <div style={styles.activityItem}>
                      <span style={{ color: '#f87171', marginRight: '8px' }}></span>
                      <span style={{ color: '#d1d5db' }}>Producto agotado</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
