import React, { useState, useEffect } from "react";
import axios from "axios";
import "./perfil.css";

const API_URL = "http://localhost:6000";

const Perfil = () => {
  const [user, setUser] = useState({ nombre: "", correo: "", foto: "" });
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");
  const [fotoFile, setFotoFile] = useState(null);
  const [mensaje, setMensaje] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) return;
    axios
      .get(`${API_URL}/api/profile`, { headers: { Authorization: `Bearer ${token}` } })
      .then((res) => {
        setUser(res.data);
        setNombre(res.data.nombre);
        setCorreo(res.data.correo);
      })
      .catch(() => setMensaje("❌ Error al cargar perfil"));
  }, [token]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/api/profile`, { nombre, correo }, { headers: { Authorization: `Bearer ${token}` } });
      setUser({ ...user, nombre, correo });
      setMensaje("✅ Perfil actualizado");
    } catch {
      setMensaje("❌ Error al actualizar perfil");
    }
  };

  const handleUploadPhoto = async (e) => {
    e.preventDefault();
    if (!fotoFile) return;
    const formData = new FormData();
    formData.append("foto", fotoFile);
    try {
      const res = await axios.post(`${API_URL}/api/profile/photo`, formData, {
        headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" },
      });
      setUser({ ...user, foto: res.data.foto });
      setMensaje("✅ Foto actualizada");
    } catch {
      setMensaje("❌ Error al subir foto");
    }
  };

  return (
    <div className="perfil-container">
      {/* Sidebar */}
      <div className="perfil-sidebar">
        <h2>PERFIL</h2>
        <img src={user.foto ? `${API_URL}${user.foto}` : "/default-avatar.png"} alt="Foto de perfil" />
        <p><strong>Nombre:</strong> {user.nombre}</p>
        <p><strong>Correo:</strong> {user.correo}</p>
      </div>

      {/* Main */}
      <div className="perfil-main">
        <h2>Editar Perfil</h2>
        {mensaje && <p className="mensaje">{mensaje}</p>}

        <form className="perfil-form" onSubmit={handleUpdateProfile}>
          <label>Nombre:</label>
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
          <label>Correo:</label>
          <input type="email" value={correo} onChange={(e) => setCorreo(e.target.value)} />
          <button type="submit" className="guardar">Guardar Cambios</button>
        </form>

        <form className="perfil-form" onSubmit={handleUploadPhoto}>
          <label>Subir Foto de Perfil:</label>
          <input type="file" onChange={(e) => setFotoFile(e.target.files[0])} />
          <button type="submit" className="subir">Subir Foto</button>
        </form>
      </div>
    </div>
  );
};

export default Perfil;
