import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./metodoPago.css";

function MetodoPago() {
  const [metodo, setMetodo] = useState("");
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");
  const [pedidoInfo, setPedidoInfo] = useState(null);
  const [procesandoPago, setProcesandoPago] = useState(false);

  const navigate = useNavigate();

  // Cargar información del pedido desde localStorage
  useEffect(() => {
    const pedido = JSON.parse(localStorage.getItem("pedidoActual"));
    console.log("📦 [METODO PAGO] Pedido recibido:", pedido);
    
    if (pedido) {
      setPedidoInfo(pedido);
    } else {
      // Si no hay pedido, redirigir al carrito
      console.log("❌ [METODO PAGO] No hay pedido, redirigiendo al carrito");
      navigate('/cart');
    }
  }, [navigate]);

  const datosPago = {
    "Banco Pichincha": {
      descripcion: "Transferencia bancaria a la siguiente cuenta:",
      cuenta: "Cuenta Corriente: 2200154789",
      titular: "Juan Pérez",
      cedula: "Cédula: 0102030405",
      imagen: "/imagenes/BancoPichincha.png",
      tipo: "transferencia",
      comision: 0
    },
    "De Una": {
      descripcion: "Pago rápido a través de la app De Una:",
      cuenta: "Número de cuenta: 0998765432",
      titular: "Maria Gómez",
      cedula: "Cédula: 1102030405",
      imagen: "/imagenes/DeUna!.png",
      tipo: "digital",
      comision: 0
    },
    "Produbanco": {
      descripcion: "Transferencia a Produbanco:",
      cuenta: "Cuenta Ahorros: 4567891230",
      titular: "Carlos Torres",
      cedula: "Cédula: 1702030405",
      imagen: "/imagenes/Produbanco.png",
      tipo: "transferencia",
      comision: 0
    },
    "Efectivo": {
      descripcion: "Paga en efectivo al momento de recibir el producto o en nuestras oficinas.",
      cuenta: "",
      titular: "",
      cedula: "",
      imagen: "/imagenes/dinero-removebg-preview.png",
      tipo: "efectivo",
      comision: 2.50 // Comisión por servicio de entrega
    }
  };

  const handleSubmit = async (e) => {   
    e.preventDefault();
    
    if (!metodo) {
      mostrarMensaje("Por favor, selecciona un método de pago.", "error");
      return;
    }
    if (!nombre.trim() || !correo.trim()) {
      mostrarMensaje("Por favor, ingresa tu nombre y correo.", "error");
      return;
    }

    setProcesandoPago(true);

    // Simular procesamiento
    setTimeout(() => {
      const pagoCompleto = {
        ...pedidoInfo,
        metodoPago: metodo,
        cliente: { nombre, correo },
        fecha: new Date().toISOString(),
        numeroPedido: `PED-${Date.now()}`,
        estado: "confirmado"
      };

      console.log("✅ [METODO PAGO] Pago completado:", pagoCompleto);

      // Limpiar datos temporales
      localStorage.removeItem("pedidoActual");
      localStorage.removeItem("carrito");

      // Mostrar mensaje de éxito
      mostrarMensaje("¡Pago confirmado con éxito! Recibirás un email con los detalles.", "success");

      // Redirigir después de un momento
      setTimeout(() => {
        navigate('/tienda');
      }, 3000);

      setProcesandoPago(false);
    }, 2000);
  };

  const mostrarMensaje = (texto, tipo) => {
    const mensaje = document.createElement('div');
    mensaje.className = `mp-mensaje mp-mensaje-${tipo}`;
    mensaje.textContent = texto;
    document.body.appendChild(mensaje);

    setTimeout(() => {
      if (document.body.contains(mensaje)) {
        document.body.removeChild(mensaje);
      }
    }, 5000);
  };

  const calcularTotalConComision = () => {
    if (!pedidoInfo || !metodo) return 0;
    const comision = datosPago[metodo]?.comision || 0;
    return (pedidoInfo.total + comision).toFixed(2);
  };

  // Opción método
  const crearOpcionMetodo = (opcion) => (
    <label
      key={opcion}
      className={`mp-metodo-option ${metodo === opcion ? "mp-active" : ""}`}
    >
      <input
        type="radio"
        value={opcion}
        checked={metodo === opcion}
        onChange={(e) => setMetodo(e.target.value)}
        className="mp-radio-input"
      />
      <div className="mp-metodo-content">
        <div className="mp-metodo-imagen-container">
          <img
            src={datosPago[opcion].imagen}
            alt={opcion}
            className="mp-metodo-imagen"
            onError={(e) => {
              e.target.src = '/imagenes/placeholder.jpg';
              e.target.style.opacity = '0.5';
            }}
          />
        </div>
        <div className="mp-metodo-info">
          <span className="mp-metodo-nombre">{opcion}</span>
          <span className="mp-metodo-tipo">{datosPago[opcion].tipo}</span>
          {datosPago[opcion].comision > 0 && (
            <span className="mp-comision">+${datosPago[opcion].comision} comisión</span>
          )}
        </div>
        <div className="mp-check-icon">
          {metodo === opcion && <span>✓</span>}
        </div>
      </div>
    </label>
  );

  // Info pago
  const crearInfoPago = () => {
    if (!metodo) return null;
    const datos = datosPago[metodo];

    return (
      <div className="mp-info-pago">
        <div className="mp-info-header">
          <img 
            src={datos.imagen} 
            alt={metodo} 
            className="mp-info-imagen"
            onError={(e) => {
              e.target.src = '/imagenes/placeholder.jpg';
              e.target.style.opacity = '0.5';
            }}
          />
          <h3>Instrucciones para {metodo}</h3>
        </div>
        <div className="mp-info-details">
          <p>{datos.descripcion}</p>
          {datos.cuenta && (
            <div className="mp-cuenta-info">
              <p><strong>{datos.cuenta}</strong></p>
            </div>
          )}
          {datos.titular && (
            <p><strong>Titular:</strong> {datos.titular}</p>
          )}
          {datos.cedula && (
            <p><strong>{datos.cedula}</strong></p>
          )}
          
          {metodo === "Efectivo" && (
            <div className="mp-efectivo-info">
              <p>📍 <strong>Dirección de oficinas:</strong></p>
              <p>Av. Principal 123, Centro Comercial Plaza, Local 45</p>
              <p>🕒 <strong>Horarios:</strong> Lunes a Viernes 9:00 AM - 6:00 PM</p>
              <p>📞 <strong>Teléfono:</strong> +593 99 123 4567</p>
            </div>
          )}
        </div>
      </div>
    );
  };

  if (!pedidoInfo) {
    return (
      <div className="mp-loading">
        <div className="mp-spinner"></div>
        <p>Cargando información del pedido...</p>
      </div>
    );
  }

  return (
    <div className="mp-container">
      <div className="mp-header">
        <button 
          onClick={() => navigate('/cart')} 
          className="mp-btn-volver"
        >
          ← Volver al Carrito
        </button>
        <h1>💳 Método de Pago</h1>
      </div>

      <div className="mp-content">
        {/* Resumen del Pedido */}
        <div className="mp-resumen-pedido">
          <h3>📋 Resumen del Pedido</h3>
          <div className="mp-productos-resumen">
            {pedidoInfo.productos.map((producto, index) => (
              <div key={index} className="mp-producto-item">
                <img 
                  src={producto.imagen || '/imagenes/placeholder.jpg'} 
                  alt={producto.nombre}
                  className="mp-producto-imagen"
                />
                <div className="mp-producto-info">
                  <span className="mp-producto-nombre">{producto.nombre}</span>
                  <span className="mp-producto-precio">${producto.precio}</span>
                </div>
              </div>
            ))}
          </div>
          
          <div className="mp-totales">
            <div className="mp-linea-total">
              <span>Subtotal:</span>
              <span>${pedidoInfo.subtotal}</span>
            </div>
            <div className="mp-linea-total">
              <span>Envío:</span>
              <span>{pedidoInfo.envio === 0 ? 'GRATIS' : `$${pedidoInfo.envio}`}</span>
            </div>
            {metodo && datosPago[metodo].comision > 0 && (
              <div className="mp-linea-total mp-comision">
                <span>Comisión {metodo}:</span>
                <span>+${datosPago[metodo].comision}</span>
              </div>
            )}
            <div className="mp-linea-total mp-total-final">
              <span><strong>Total a Pagar:</strong></span>
              <span><strong>${metodo ? calcularTotalConComision() : pedidoInfo.total}</strong></span>
            </div>
          </div>
        </div>

        {/* Formulario de Pago */}
        <form onSubmit={handleSubmit} className="mp-form">
          <h3>👤 Información del Cliente</h3>
          
          <div className="mp-inputs-container">
            <input
              type="text"
              placeholder="Ingresa tu nombre completo"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              className="mp-input"
              required
            />

            <input
              type="email"
              placeholder="Ingresa tu correo electrónico"
              value={correo}
              onChange={(e) => setCorreo(e.target.value)}
              className="mp-input"
              required
            />
          </div>

          <h3>💳 Selecciona tu Método de Pago</h3>
          <div className="mp-metodos-container">
            {Object.keys(datosPago).map((opcion) => crearOpcionMetodo(opcion))}
          </div>

          <button 
            type="submit" 
            className="mp-btn-confirmar"
            disabled={procesandoPago}
          >
            {procesandoPago ? (
              <>
                <div className="mp-btn-spinner"></div>
                Procesando pago...
              </>
            ) : (
              <>
                ✅ Confirmar Pago - ${metodo ? calcularTotalConComision() : pedidoInfo.total}
              </>
            )}
          </button>
        </form>

        {/* Información del método seleccionado */}
        {crearInfoPago()}
      </div>
    </div>
  );
}

export default MetodoPago;
