import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './home.css';

const Home = () => {
  const navigate = useNavigate();

  // Estado simple para carrito (solo demo)
  const [cartItems, setCartItems] = useState([
    { id: 1, name: 'Zapatillas Hype', price: 120 },
    { id: 2, name: 'Gorra Urbana', price: 35 },
  ]);

  const totalPrice = cartItems.reduce((total, item) => total + item.price, 0);

  // Estado para formulario de contacto
  const [contactData, setContactData] = useState({ nombre: '', email: '', mensaje: '' });
  const [formStatus, setFormStatus] = useState('');

  const handleContactChange = (e) => {
    const { name, value } = e.target;
    setContactData(prev => ({ ...prev, [name]: value }));
  };

  const handleContactSubmit = (e) => {
    e.preventDefault();
    // Aquí podrías enviar a un backend o mostrar mensaje
    setFormStatus('¡Gracias por contactarnos! Te responderemos pronto.');
    setContactData({ nombre: '', email: '', mensaje: '' });
  };

  return (
    <div className="home-container">
      
      {/* Navbar fijo con botón funcional */}
      <nav className="navbar">
        <div className="logo" onClick={() => navigate("/home")}>
          HYPE DISTRICT
        </div>
        <ul className="nav-links">
          <li onClick={() => navigate("/home")}>Inicio</li>
          <li onClick={() => navigate("/registrarse")}>Registrarse</li>
          <li onClick={() => navigate("/")}>Iniciar Sesión</li>
        </ul>
      </nav>

      {/* Contenido principal sin botón */}
      <div className="hero" style={{ marginTop: "120px" }}>
        <h1>HYPE DISTRICT</h1>
        <p>La cultura urbana se vive aquí. Encuentra tu estilo, tus zapatillas, tu identidad.</p>
      </div>

      <div className="destacados">
        <div className="card">
          <h3>🔥 Zapatillas Exclusivas</h3>
          <p>Modelos únicos y coleccionables</p>
        </div>
        <div className="card">
          <h3>🎧 Estilo Urbano</h3>
          <p>Ropa y accesorios con flow callejero</p>
        </div>
        <div className="card">
          <h3>🧢 Comunidad</h3>
          <p>Únete a miles que viven el hype</p>
        </div>
      </div>

      {/* Sección Carrito */}
      <div className="carrito">
        <h2>🛒 Tu Carrito</h2>
        {cartItems.length === 0 ? (
          <p>Tu carrito está vacío.</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id}>
                {item.name} - ${item.price}
              </li>
            ))}
          </ul>
        )}
        <h3>Total: ${totalPrice}</h3>
      </div>

      {/* Sección Contáctanos */}
      <div className="contactanos">
        <h2>📩 Contáctanos</h2>
        <form onSubmit={handleContactSubmit}>
          <label>
            Nombre:
            <input
              type="text"
              name="nombre"
              value={contactData.nombre}
              onChange={handleContactChange}
              required
            />
          </label>
          <label>
            Correo Electrónico:
            <input
              type="email"
              name="email"
              value={contactData.email}
              onChange={handleContactChange}
              required
            />
          </label>
          <label>
            Mensaje:
            <textarea
              name="mensaje"
              value={contactData.mensaje}
              onChange={handleContactChange}
              required
            />
          </label>
          <button type="submit">Enviar</button>
        </form>
        {formStatus && <p className="form-status">{formStatus}</p>}
      </div>
    </div>
  );
};

export default Home;
