import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './tienda.css';

const Cart = () => {
  const [items, setItems] = useState([]);
  const [mensaje, setMensaje] = useState('');
  const [animando, setAnimando] = useState(null);
  const [total, setTotal] = useState(0);
  const [cantidades, setCantidades] = useState({});

  const navigate = useNavigate();

  // Cargar carrito desde localStorage
  useEffect(() => {
    const carritoGuardado = JSON.parse(localStorage.getItem("carrito")) || [];
    setItems(carritoGuardado);
    
    // Inicializar cantidades (todos con 1)
    const cantidadesIniciales = {};
    carritoGuardado.forEach((_, index) => {
      cantidadesIniciales[index] = 1;
    });
    setCantidades(cantidadesIniciales);
  }, []);

  useEffect(() => {
    const totalCalculado = items.reduce((sum, item, index) => {
      const cantidad = cantidades[index] || 1;
      return sum + (parseFloat(item.precio) * cantidad);
    }, 0);
    setTotal(totalCalculado.toFixed(2));
    
    // Guardar cambios en localStorage
    localStorage.setItem("carrito", JSON.stringify(items));
  }, [items, cantidades]);

  // Cambiar cantidad de un producto
  const cambiarCantidad = (index, nuevaCantidad) => {
    if (nuevaCantidad < 1) return;
    setCantidades(prev => ({
      ...prev,
      [index]: nuevaCantidad
    }));
  };

  const handleEliminar = (index) => {
    setAnimando(index);
    setTimeout(() => {
      const nuevoCarrito = [...items];
      const nuevasCantidades = { ...cantidades };
      
      nuevoCarrito.splice(index, 1);
      delete nuevasCantidades[index];
      
      // Reajustar Ã­ndices de cantidades
      const cantidadesReajustadas = {};
      Object.keys(nuevasCantidades).forEach(key => {
        const oldIndex = parseInt(key);
        const newIndex = oldIndex > index ? oldIndex - 1 : oldIndex;
        if (newIndex >= 0) {
          cantidadesReajustadas[newIndex] = nuevasCantidades[key];
        }
      });
      
      setItems(nuevoCarrito);
      setCantidades(cantidadesReajustadas);
      setMensaje('ğŸ—‘ï¸ Producto eliminado del carrito');
      setAnimando(null);
      setTimeout(() => setMensaje(''), 2500);
    }, 400);
  };

  const handlePagar = () => {
    if (items.length === 0) {
      setMensaje('âŒ No hay productos para pagar');
      setTimeout(() => setMensaje(''), 2500);
      return;
    }
    
    // Simular proceso de pago
    setMensaje('ğŸ’³ Procesando pago...');
    setTimeout(() => {
      setMensaje('âœ… Â¡Compra realizada con Ã©xito!');
      setTimeout(() => {
        setItems([]);
        setCantidades({});
        localStorage.removeItem("carrito");
        setMensaje('ğŸ“¦ RecibirÃ¡s tu pedido pronto!');
        setTimeout(() => setMensaje(''), 3000);
      }, 2000);
    }, 1500);
  };

  const handleVaciarCarrito = () => {
    if (items.length === 0) {
      setMensaje('âŒ El carrito ya estÃ¡ vacÃ­o');
      setTimeout(() => setMensaje(''), 2500);
      return;
    }
    
    setItems([]);
    setCantidades({});
    localStorage.removeItem("carrito");
    setMensaje('ğŸ§¹ Carrito vaciado');
    setTimeout(() => setMensaje(''), 2000);
  };

  const continuarComprando = () => {
    navigate('/tienda');
  };

  return (
    <div className="carrito-container" style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%)',
      padding: '20px'
    }}>
      {/* Header del carrito */}
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '10px',
        padding: '20px',
        marginBottom: '20px',
        textAlign: 'center'
      }}>
        <h1 style={{ color: '#fff', margin: '0', fontSize: '2rem' }}>
          ğŸ›’ Carrito de Compras
        </h1>
        <p style={{ color: '#ccc', margin: '10px 0 0 0' }}>
          {items.length} {items.length === 1 ? 'producto' : 'productos'} en tu carrito
        </p>
      </div>

      {/* Mensaje flotante */}
      {mensaje && (
        <div style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          background: mensaje.includes('âŒ') ? '#dc3545' : mensaje.includes('âœ…') ? '#28a745' : '#007bff',
          color: 'white',
          padding: '15px 20px',
          borderRadius: '8px',
          zIndex: 10000,
          fontWeight: 'bold',
          boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
          animation: 'slideInRight 0.3s ease'
        }}>
          {mensaje}
        </div>
      )}

      <div className="carrito-content" style={{ 
        display: 'flex', 
        gap: '20px', 
        flexWrap: 'wrap' 
      }}>
        {/* Lista de productos */}
        <div style={{ 
          flex: '2', 
          minWidth: '300px'
        }}>
          {items.length === 0 ? (
            <div style={{
              background: 'rgba(255,255,255,0.05)',
              borderRadius: '10px',
              padding: '60px 20px',
              textAlign: 'center',
              color: '#fff'
            }}>
              <div style={{ fontSize: '4rem', marginBottom: '20px' }}>ğŸ›’</div>
              <h3>Tu carrito estÃ¡ vacÃ­o</h3>
              <p style={{ color: '#ccc', margin: '10px 0 20px 0' }}>
                Agrega algunos productos increÃ­bles desde nuestra tienda
              </p>
              <button 
                onClick={continuarComprando}
                style={{
                  background: '#007bff',
                  color: 'white',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '25px',
                  fontSize: '16px',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease'
                }}
                onMouseOver={(e) => e.target.style.background = '#0056b3'}
                onMouseOut={(e) => e.target.style.background = '#007bff'}
              >
                ğŸ›ï¸ Ir a la Tienda
              </button>
            </div>
          ) : (
            <div className="carrito-lista">
              {items.map((item, index) => (
                <div
                  key={index}
                  className={`carrito-item ${animando === index ? 'eliminando' : ''}`}
                  style={{
                    background: 'rgba(255,255,255,0.08)',
                    borderRadius: '12px',
                    padding: '20px',
                    marginBottom: '15px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '15px',
                    transition: 'all 0.3s ease',
                    border: '1px solid rgba(255,255,255,0.1)'
                  }}
                >
                  {/* Imagen del producto */}
                  <div style={{ flexShrink: 0 }}>
                    <img 
                      src={item.imagen || '/imagenes/placeholder.jpg'} 
                      alt={item.nombre}
                      style={{
                        width: '80px',
                        height: '80px',
                        borderRadius: '8px',
                        objectFit: 'cover',
                        border: '2px solid rgba(255,255,255,0.1)'
                      }}
                      onError={(e) => {
                        e.target.src = '/imagenes/placeholder.jpg';
                      }}
                    />
                  </div>

                  {/* InformaciÃ³n del producto */}
                  <div style={{ flex: '1', color: '#fff' }}>
                    <h4 style={{ margin: '0 0 5px 0', fontSize: '1.1rem' }}>
                      {item.nombre}
                    </h4>
                    {item.categoria && (
                      <p style={{ 
                        margin: '0 0 5px 0', 
                        color: '#ccc', 
                        fontSize: '0.9rem',
                        textTransform: 'capitalize'
                      }}>
                        ğŸ“‚ {item.categoria}
                      </p>
                    )}
                    <p style={{ 
                      margin: '0', 
                      fontSize: '1.2rem', 
                      fontWeight: 'bold',
                      color: '#28a745'
                    }}>
                      ${parseFloat(item.precio).toFixed(2)} c/u
                    </p>
                  </div>

                  {/* Controles de cantidad */}
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '10px',
                    background: 'rgba(255,255,255,0.1)',
                    padding: '8px 12px',
                    borderRadius: '25px'
                  }}>
                    <button
                      onClick={() => cambiarCantidad(index, (cantidades[index] || 1) - 1)}
                      style={{
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        borderRadius: '50%',
                        width: '30px',
                        height: '30px',
                        cursor: 'pointer',
                        fontSize: '18px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}
                    >
                      âˆ’
                    </button>
                    
                    <span style={{ 
                      color: '#fff', 
                      fontSize: '1.1rem', 
                      fontWeight: 'bold',
                      minWidth: '30px',
                      textAlign: 'center'
                    }}>
                      {cantidades[index] || 1}
                    </span>
                    
                    <button
                      onClick={() => cambiarCantidad(index, (cantidades[index] || 1) + 1)}
                      style={{
                        background: '#28a745',
                        color: 'white',
                        border: 'none',
                        borderRadius: '50%',
                        width: '30px',
                        height: '30px',
                        cursor: 'pointer',
                        fontSize: '18px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center'
                      }}
                    >
                      +
                    </button>
                  </div>

                  {/* Subtotal y eliminar */}
                  <div style={{ 
                    textAlign: 'right',
                    minWidth: '100px'
                  }}>
                    <p style={{ 
                      margin: '0 0 10px 0', 
                      color: '#fff', 
                      fontSize: '1.1rem',
                      fontWeight: 'bold'
                    }}>
                      ${((parseFloat(item.precio) * (cantidades[index] || 1)).toFixed(2))}
                    </p>
                    <button 
                      className="btn-eliminar" 
                      onClick={() => handleEliminar(index)}
                      style={{
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        padding: '8px 12px',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        fontSize: '12px',
                        fontWeight: 'bold'
                      }}
                    >
                      ğŸ—‘ï¸ Eliminar
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Resumen del pedido */}
        {items.length > 0 && (
          <div style={{ 
            flex: '1', 
            minWidth: '280px',
            maxWidth: '400px'
          }}>
            <div style={{
              background: 'rgba(255,255,255,0.08)',
              borderRadius: '12px',
              padding: '25px',
              border: '1px solid rgba(255,255,255,0.1)',
              position: 'sticky',
              top: '20px'
            }}>
              <h3 style={{ color: '#fff', marginTop: '0', marginBottom: '20px' }}>
                ğŸ“‹ Resumen del Pedido
              </h3>
              
              {/* Desglose */}
              <div style={{ marginBottom: '20px' }}>
                {items.map((item, index) => (
                  <div key={index} style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px 0',
                    borderBottom: '1px solid rgba(255,255,255,0.1)',
                    color: '#ccc',
                    fontSize: '14px'
                  }}>
                    <span>
                      {item.nombre.length > 20 ? item.nombre.substring(0, 20) + '...' : item.nombre}
                      <span style={{ color: '#999' }}> x{cantidades[index] || 1}</span>
                    </span>
                    <span style={{ color: '#fff', fontWeight: 'bold' }}>
                      ${((parseFloat(item.precio) * (cantidades[index] || 1)).toFixed(2))}
                    </span>
                  </div>
                ))}
              </div>

              {/* Total */}
              <div style={{
                borderTop: '2px solid rgba(255,255,255,0.2)',
                paddingTop: '15px',
                marginBottom: '25px'
              }}>
                <div style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  color: '#fff'
                }}>
                  <span style={{ fontSize: '1.3rem', fontWeight: 'bold' }}>
                    ğŸ’° Total:
                  </span>
                  <span style={{ 
                    fontSize: '1.5rem', 
                    fontWeight: 'bold',
                    color: '#28a745'
                  }}>
                    ${total}
                  </span>
                </div>
                <p style={{ 
                  color: '#999', 
                  fontSize: '12px', 
                  margin: '5px 0 0 0',
                  textAlign: 'center'
                }}>
                  IVA incluido â€¢ EnvÃ­o gratis
                </p>
              </div>

              {/* Botones de acciÃ³n */}
              <div style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
                <button 
                  className="btn-pagar" 
                  onClick={handlePagar}
                  style={{
                    background: 'linear-gradient(45deg, #28a745, #20c997)',
                    color: 'white',
                    border: 'none',
                    padding: '15px',
                    borderRadius: '8px',
                    fontSize: '16px',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    boxShadow: '0 4px 15px rgba(40, 167, 69, 0.3)'
                  }}
                  onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
                  onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
                >
                  ğŸ’³ Pagar Ahora
                </button>
                
                <button 
                  onClick={continuarComprando}
                  style={{
                    background: 'transparent',
                    color: '#007bff',
                    border: '2px solid #007bff',
                    padding: '12px',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: 'bold',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseOver={(e) => {
                    e.target.style.background = '#007bff';
                    e.target.style.color = 'white';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.background = 'transparent';
                    e.target.style.color = '#007bff';
                  }}
                >
                  ğŸ›ï¸ Continuar Comprando
                </button>

                <button 
                  className="btn-vaciar" 
                  onClick={handleVaciarCarrito}
                  style={{
                    background: 'transparent',
                    color: '#dc3545',
                    border: '1px solid #dc3545',
                    padding: '10px',
                    borderRadius: '6px',
                    fontSize: '13px',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseOver={(e) => {
                    e.target.style.background = '#dc3545';
                    e.target.style.color = 'white';
                  }}
                  onMouseOut={(e) => {
                    e.target.style.background = 'transparent';
                    e.target.style.color = '#dc3545';
                  }}
                >
                  ğŸ§¹ Vaciar Carrito
                </button>
              </div>

              {/* InformaciÃ³n adicional */}
              <div style={{
                marginTop: '20px',
                padding: '15px',
                background: 'rgba(0, 123, 255, 0.1)',
                borderRadius: '8px',
                border: '1px solid rgba(0, 123, 255, 0.2)'
              }}>
                <h5 style={{ color: '#007bff', margin: '0 0 8px 0', fontSize: '14px' }}>
                  â„¹ï¸ InformaciÃ³n del pedido:
                </h5>
                <ul style={{ 
                  color: '#ccc', 
                  fontSize: '12px', 
                  margin: '0',
                  paddingLeft: '15px',
                  lineHeight: '1.6'
                }}>
                  <li>EnvÃ­o gratis en compras superiores a $50</li>
                  <li>Entrega estimada: 2-3 dÃ­as hÃ¡biles</li>
                  <li>Pago seguro garantizado</li>
                  <li>Devoluciones hasta 30 dÃ­as</li>
                </ul>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* CSS para animaciones */}
      <style jsx>{`
        @keyframes slideInRight {
          from {
            transform: translateX(100%);
            opacity: 0;
          }
          to {
            transform: translateX(0);
            opacity: 1;
          }
        }

        .eliminando {
          animation: fadeOut 0.4s ease forwards;
        }

        @keyframes fadeOut {
          from {
            opacity: 1;
            transform: translateX(0);
          }
          to {
            opacity: 0;
            transform: translateX(-100%);
          }
        }

        .carrito-container {
          font-family: 'Arial', sans-serif;
        }

        @media (max-width: 768px) {
          .carrito-content {
            flex-direction: column;
          }
          
          .carrito-item {
            flex-direction: column;
            text-align: center;
            gap: 10px !important;
          }
          
          .carrito-item img {
            width: 60px !important;
            height: 60px !important;
          }
        }
      `}</style>
    </div>
  );
